        .tag-container {
            display: flex;
            flex-wrap: wrap;
            align-items: center;
            padding: 8px;
            border: 1px solid #A7A7A7;
            border-radius: 4px;
            height: 100%;
        }

        .tag {
            display: inline-flex;
            align-items: center;
            background-color: #e1f5fe;
