        document.addEventListener('DOMContentLoaded', function() {
            // Initialize the editable area
            checkEmpty('editor');

            // Add event listener for focus
            document.getElementById('editor').addEventListener('focus', function() {
                if (this.getAttribute('data-empty') === 'true') {
                    this.setAttribute('data-empty', 'false');
                    this.textContent = '';
                }
            });
        });

        // Function to check if the editable area is empty
        function checkEmpty(id) {
            const element = document.getElementById(id);
            if (!element) return;

            const isEmpty = element.textContent.trim() === '';
            element.setAttribute('data-empty', isEmpty ? 'true' : 'false');
        }

        // Function to apply formatting to selected text
        function formatText(type) {
            // Get the selection
            const selection = window.getSelection();

            // Check if something is selected
            if (selection.rangeCount === 0) {
                return;
            }

            // For list formatting, we'll handle it even if no text is selected
            if (type === 'list' || selection.toString().length > 0) {
                // Format based on type
                switch(type) {
                    case 'bold':
                        document.execCommand('bold', false, null);
                        break;
                    case 'italic':
                        document.execCommand('italic', false, null);
                        break;
                    case 'underline':
                        document.execCommand('underline', false, null);
                        break;
                    case 'list':
                        document.execCommand('insertUnorderedList', false, null);
                        break;
                    case 'highlight':
                        document.execCommand('backColor', false, '#fff3cd');
                        break;
                }
            }

            // Make sure to re-focus on the editable area
            document.getElementById('editor').focus();
        }

        // Add keyboard shortcuts
        document.addEventListener('keydown', function(e) {
            if (e.ctrlKey || e.metaKey) {
                if (e.key === 'b') {
                    e.preventDefault();
                    formatText('bold');
                } else if (e.key === 'i') {
                    e.preventDefault();
                    formatText('italic');
                } else if (e.key === 'u') {
                    e.preventDefault();
                    formatText('underline');
                }
            }
        });
    </script>
